INSERT INTO `legacy_web_indicators` VALUES ('1', '1', '2', 'indicator1 result prova titolo', '', '', '', '', '0'), ('2', '1', '2', 'titolo 2', '', '', '', '', '0'), ('83', '16', '7917', 'Prevalence of wasting among children under 5 years of age by sex (SDG indicator 2.2.2)', 'Percentage', '<p>Wasting is defined as: weight for height &lt;-2 SD from the median of the WHO Child Growth Standards; -3 SD is the cut-off for severe wasting</p>\r\n', '<p><a href=\"http://www.who.int/nutgrowthdb/estimates2012/en/\" target=\"_blank\">www.who.int/nutgrowthdb/estimates2012/en/ (WHO-UNICEF-World Bank)</a></p>\r\n\r\n<p>SDG Reporting on indicator 2.2.2: <a href=\"http://www.who.int/nutgrowthdb/estimates2012/en/\" target=\"_blank\">http://unstats.un.org/sdgs/indicators/database</a></p>\r\n', 'secondary', '0'), ('85', '16', '7921', 'Volume of ODA funding towards Nutrition (by donor and by recipient country)', 'Currency', '<p>Methodology underlying the estimations can be found at donors4Nutrition http://donors4nutrition.r4d.org/assets/pdf/R4D_Methodology.pdf</p>\r\n', '<p>OECD ODA Reporting</p>\r\n\r\n<p><a href=\"http://donors4nutrition.r4d.org/\">Donors4Nutrition</a> Results for Development charts individual countries&rsquo; ODA and provides a breakdown of current donor financing for nutrition.</p>\r\n', 'secondary', '0'), ('90', '16', '7915', 'Status of a budgeted national multi-sector plan/strategy for nutrition ', '', '<p><em>Score for this indicator will have to be based on an expert assessment (by Unit C1), and there need to be clear assessment criteria (agreed with partner countries) &ndash; for example as part of the update of Nutrition country fiches</em></p>\r\n', '<p>Monitored by DEVCO HQ (C1)</p>\r\n\r\n<p>Nutrition country fiche</p>\r\n', 'secondary and expert assessment', '0'), ('119', '16', '7884', 'Value or quantity of smallholders’ sales by sex', 'currency or tonnes ', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('11', '16', '7904', 'Prevalence of waterborne diseases by sex', 'Percentage ', '<p>Please define your target population for measurement before the start of the programme</p>\r\n', '<p>DHS <a href=\"http://dhsprogram.com/\">http://dhsprogram.com/</a> - Maternal and Child Health</p>\r\n\r\n<p>National statistics</p>\r\n', 'secondary', '0'), ('12', '16', '7904', 'Prevalence of anaemia in pregnant women', 'Percentage', '<p>Proportion of pregnant women with a haemoglobin level below 110g/litre (severe anaemia &lt; 100 g/litre) (Tools &amp; Methods p. 77)</p>\r\n\r\n<p>Please define your target population for measurement before the start of the programme7)</p>\r\n', '<p>National statistics or estimates , if available</p>\r\n\r\n<p>The <a href=\"http://databank.worldbank.org/data/reports.aspx?source=2&amp;series=SH.PRG.ANEM&amp;country=\">World Bank</a> has a database, but last data available date back to 2011</p>\r\n\r\n<p>WHO- Vitamin and Mineral Nutrition Information System (VMNIS) <a href=\"http://www.who.int/vmnis/database/en/\">http://www.who.int/vmnis/database/en/</a> : a database on biomarkers of vitamin and mineral nutrition in populations, including anaemia, iodine, iron, and vitamin A deficiencies. A new version of the database is expected for end of December 2016.</p>\r\n\r\n<p>The <a href=\"http://dhsprogram.com/data/DHS-Survey-Indicators-Maternal-and-Child-Nutrition.cfm\">DHS programme</a> includes an indicator on the prevalence of anaemia in women aged 15-49</p>\r\n', 'secondary', '0'), ('13', '16', '7904', 'Prevalence of micronutrient deficiencies (vitamin A, zinc, iron, and iodine) among women of reproductive age and/or children', 'Percentage', '<p>Please define your target population for measurement before the start of the programme</p>\r\n\r\n<p>Information on the sex of the children should also be collected</p>\r\n', '<p>&nbsp;Data from the National health information system, if available</p>\r\n\r\n<p><a href=\"http://www.who.int/vmnis/database/en/\">WHO VMNIS</a></p>\r\n', 'secondary', '0'), ('99', '16', '7887', 'Minimum Dietary Diversity – Women -  Proportion of women of reproductive age (15-49 years) have consumed at least five good groups (based on the MDD-W methodology)', 'Percentage', '<p>MDD-C definitions and methodology can be found at <a href=\"http://www.fao.org/documents/card/en/c/57157ba0-91a7-4087-ab59-d922d7020cc2/\">Minimum Dietary Diversity for Women: A guide to measurement</a>. FAO and FANTA III, 2016.</p>\r\n\r\n<p>Please define your target population for measurement before the start of the programme</p>\r\n', '<p>UNICEF - MICS5 <a href=\"http://mics.unicef.org/\">http://mics.unicef.org/</a></p>\r\n', 'secondary or primary', '0'), ('100', '16', '7887', 'Minimum Dietary Diversity Proportion of children 6–23 months of age who receive foods from 4 or more food groups (based the  MDD-C methodology) by sex', 'Percentage', '<p>MDD-C definitions and methodology can be found at: <a href=\"http://www.who.int/maternal_child_adolescent/documents/9789241596664/en/\">Indicators for assessing infant and young child feeding practices</a>. WHO, 2010.</p>\r\n\r\n<p>For more information, please refer to <a href=\"http://www.who.int/maternal_child_adolescent/documents/9789241596664/en/\">Indicators for assessing infant and young child feeding practices</a>. WHO, 2010.</p>\r\n\r\n<p>Please define your target population for measurement before the start of the programme</p>\r\n', '<p><em>From national statistics (currently under development by FAO and piloted by USAID)</em></p>\r\n', 'secondary or primary', '0'), ('101', '16', '7887', 'Minimum Acceptable Diet (MAD): Proportion of children 6–23 months of age who receive a minimum acceptable diet (apart from breast milk) based on MAD methodology by sex', 'Percentage ', '<p>This indicator is calculated from the following two: (1) proportion of <strong>breastfed</strong> children 6-23 months of age who had at least the minimum dietary diversity and the minimum meal frequency during the previous day, and (2) proportion of <strong>non-breastfed</strong> children 6-23 months of age who had at least the minimum dietary diversity and the minimum meal frequency during the previous day (Tools &amp; Methods p. 79)</p>\r\n\r\n<p>This indicator combines standards of (a) dietary diversity (a proxy for nutrient density) and (b) feeding frequency (a proxy for energy density) by breastfeeding status; and thus provides a useful way to track progress at simultaneously improving the key quality and quantity dimensions of children&#39;s diets. (FAO Compendium p. 45)</p>\r\n\r\n<p>For more information, please refer to <a href=\"http://www.who.int/maternal_child_adolescent/documents/9789241596664/en/\">Indicators for assessing infant and young child feeding practices</a>. WHO, 2010.</p>\r\n\r\n<p>Please define your target population for measurement before the start of the programme</p>\r\n', '<p>UNICEF Data on Child Nutrition - Infant and Young Child Feeding <a href=\"https://data.unicef.org/topic/nutrition/infant-and-young-child-feeding/\">https://data.unicef.org/topic/nutrition/infant-and-young-child-feeding/</a></p>\r\n\r\n<p>DHS <a href=\"http://dhsprogram.com/\">http://dhsprogram.com/</a> - Maternal and Child Nutrition</p>\r\n', 'secondary or primary', '0'), ('122', '16', '7886', 'Number of hectares of where land management practices has been introduced with EU support.', 'Hectares', '<p>Sustainable land management practices encompass a vast array of agricultural techniques. Although non exhaustive, the main practices to be considered are: i)Rational use of mineral fertilisers, ii)Integrated pest management, iii)Improved soil organic matter, iv)Grassland development, improvement and rehabilitation, v)Management of crop rotation, vi)Cultivation of green cover crops, vii)Rainwater harvesting, vii)Irrigation, viii)Agroforestry (land-use systems and practices where woody perennials are deliberately integrated with crops and/or animals on the same land management unit).</p>\r\n\r\n<p>Conservation agriculture (combination of minimum mechanical soil disturbance, managing the top soil to create a permanent organic soil cover, and crop rotation with more than two crop species). (<a href=\"http://capacity4dev.ec.europa.eu/eu-rfi\">EURF Level 2 Indicator 6</a>)</p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('20', '16', '7903', 'Proportion of children under one year old fully immunised (DTP3) by sex', 'Percentage ', '<p>The EURF Level 2 indicator (Health) refers to the number of infants who have received three doses of DPT (diphtheria, tetanus, pertussis/whooping cough) or three doses of pentavalent vaccine (= DTP + Hepatitis B and Haemophilus influenza type b) by age one year, with EU support.</p>\r\n', '<p>WHO-UNICEF estimates (<a href=\"http://apps.who.int/immunization_monitoring/globalsummary/timeseries/tswucoveragedtp3.html\">http://apps.who.int/immunization_monitoring/globalsummary/timeseries/tswucoveragedtp3.html</a>)</p>\r\n\r\n<p>UNICEF - MICS5 <a href=\"http://mics.unicef.org/\">http://mics.unicef.org/</a></p>\r\n', 'secondary', '0'), ('121', '16', '7886', 'Number of hectares where land managed using a) improved technologies and b) improved management practices ', 'Hectares', '<p>The term technology needs to be defined and could include a change in practices compared to those currently used (e.g. seed preparation, planting time, feeding schedule, feeding ingredients, post-harvest, storage, processing, etc.) (depending on the project context)</p>\r\n', '<p>FAO and national research system statistics, as well as project M&amp;E system</p>\r\n', 'primary', '0'), ('114', '16', '7898', 'Proportion of population using safely managed drinking water services by sex (SDG 6.1.1) ', 'Percentage of population', '', '<p><a href=\"http://unstats.un.org/sdgs/indicators/database/\">http://unstats.un.org/sdgs/indicators/database/</a></p>\r\n', 'secondary', '0'), ('115', '16', '7898', 'Proportion of population using safely managed sanitation services, including a hand-washing facility with soap and water by sex (SDG 6.2.1) ', 'Percentage of population', '', '<p><a href=\"http://unstats.un.org/sdgs/indicators/database/\">http://unstats.un.org/sdgs/indicators/database/</a></p>\r\n', 'secondary', '0'), ('103', '16', '7885', 'Total volume crop production', 'Tones/kgs', '<p>These indicators might relate to national/regional or on-farm production/yield/crop diversification.</p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'secondary or primary', '0'), ('104', '16', '7885', 'Average crop yield ', 'Kg/hectare', '<p>These indicators might relate to national/regional or on-farm production/yield/crop diversification.</p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'secondary or primary', '0'), ('107', '16', '7888', 'Prices of specific foods of interest (context-specific)', 'Currency', '', '<p>Market / Price Information System when they exist. Prices of staple foods are often monitored (e.g. WFP: <a href=\"http://foodprices.vam.wfp.org/\">http://foodprices.vam.wfp.org/</a> and FAO:&nbsp; <a href=\"http://faostat3.fao.org/download/P/CP/E\">http://faostat3.fao.org/download/P/CP/E</a>)</p>\r\n', 'secondary', '0'), ('108', '16', '7888', 'Cost of a healthy diet', 'Currency', '<p>At the moment the cost of a healthy diet is only addressed by the Save the Children methodology. <a href=\"http://www.savethechildren.org.uk/resources/online-library/the-cost-of-the-diet\">http://www.savethechildren.org.uk/resources/online-library/the-cost-of-the-diet</a></p>\r\n\r\n<p><a href=\"http://www.savethechildren.org.uk/resources/online-library/the-minimum-cost-of-a-healthy-diet\">http://www.savethechildren.org.uk/resources/online-library/the-minimum-cost-of-a-healthy-diet</a></p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'secondary and primary', '0'), ('109', '16', '7888', 'Total quantity of targeted nutrient-rich value chain commodities (produced by beneficiaries) that is set aside for home consumption ', 'Number of commodities ', '<p>A commodity is defined as nutrient-rich if it meets any of the following criteria:</p>\r\n\r\n<p>1. Is bio-fortified</p>\r\n\r\n<p>2. Is a legume, nut or seed</p>\r\n\r\n<p>3. Is an animal-sourced food, including dairy products (milk, yogurt, cheese), eggs, organ meat, flesh foods, and other miscellaneous small animal protein (e.g. grubs, insects)</p>\r\n\r\n<p>4. Is a dark yellow or orange-fleshed root or tuber</p>\r\n\r\n<p>5. Is a fruit or vegetable that meets the threshold for being a &ldquo;high source&rdquo; of one or more micronutrients on a per 100 gram basis.</p>\r\n\r\n<p>A useful of list of commodities under criteria 2 through 4 may be found in the WHO document: <a href=\"http://www.who.int/maternal_child_adolescent/documents/9789241596664/en/\">Indicators for assessing infant and young child feeding practices</a>, Part 2, Measurement. (<a href=\"https://feedthefuture.gov/sites/default/files/resource/files/Feed_the_Future_Indicator_Handbook_Sept2016.pdf\">USAID Feed the Future Indicator Handbook</a> p. 97)</p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('110', '16', '7901', 'Share of women among owners or rights-bearers of agricultural land, by type of tenure (SDG 5.a.1(b))', 'Percentage', '', '<p><a href=\"http://unstats.un.org/sdgs/indicators/database/\">http://unstats.un.org/sdgs/indicators/database/</a></p>\r\n', 'secondary', '0'), ('111', '16', '7901', 'National score in the Women’s empowerment in agriculture index (IFPRI)', 'Score ', '<p>The <a href=\"http://www.ifpri.org/publication/womens-empowerment-agriculture-index\">Women&rsquo;s Empowerment in Agriculture Index (WEAI)</a> measures the empowerment, agency, and inclusion of women in the agriculture sector in five domains:</p>\r\n\r\n<p>1. decisions about agricultural production,</p>\r\n\r\n<p>2. access to and decision-making power over productive resources,</p>\r\n\r\n<p>3. control over use of income,</p>\r\n\r\n<p>4. leadership in the community, and</p>\r\n\r\n<p>5. time use.</p>\r\n', '<p>Developed and piloted by the <a href=\"http://www.ifpri.org/\">International Food Policy Research Institute (IFPRI)</a></p>\r\n', 'secondary', '0'), ('112', '16', '7901', 'Number of women and men who have secure tenure of land (with EU support)', 'Number of people', '<p>Secure land tenure covers: formal titling, certificates or registration, customary tenure secured by an appropriate legal framework that ensures that customary rights cannot be negated and the farmers cannot be arbitrarily dispossessed of their land. They can be secured by a variety of approaches, from legal to local actions.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>The definitions of terms used in this proposed target (and indicators) are consistent with the Voluntary Guidelines on the Responsible Governance of Tenure of Land, Fisheries and Forests, and the Framework and Guidelines for Land Policy in Africa. (<a href=\"http://capacity4dev.ec.europa.eu/eu-rfi\">EURF Level 2 Indicator 8</a>)</p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('113', '16', '7902', 'Number of households  targeted by the project that report a change in their income and/or have access to new sources of income by sex of head of household', 'Number of households  ', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('137', '16', '7896', 'Number of food insecure people receiving assistance through social transfers supported by the EU by sex', 'Number of people ', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('138', '16', '7896', 'Number of women who have been trained in child health and nutrition', 'Number of women ', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('139', '16', '7896', 'Number of women who have received information on feeding infants', 'Number of women', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('140', '16', '7896', 'Number of women who report increased knowledge of infant and child feeding practices', 'Number of women', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('134', '16', '7895', 'Number of farmers/agro-producers with access to credit by sex', 'Number of farmers/agro-producers', '', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('39', '16', '7897', 'Land managed using  a) improved technologies and  b) improved management practices (in hectares)', '', '<p>The term technology needs to be defined and could include a change in practices compared to those currently used (e.g. seed preparation, planting time, feeding schedule, feeding ingredients, post-harvest, storage, processing, etc.) (depending on the project context)</p>\r\n', '<p>FAO and national research system statistics, as well as project M&amp;E system<br />\r\n&nbsp;&nbsp; &nbsp;Agriculture and pastoral ecosystems where sustainable land management practices have been introduced with EU support (number of hectares)</p>\r\n', 'secondary or primary', '0'), ('40', '16', '7897', 'Agriculture and pastoral ecosystems where sustainable land management practices have been introduced with EU support (number of hectares).', '', '<p>Sustainable land management practices encompass a vast array of agricultural techniques. Although non exhaustive, the main practices to be considered are: &bull;<br />\r\nRational use of mineral fertilisers<br />\r\nIntegrated pest management<br />\r\nImproved soil organic matter<br />\r\nGrassland development, improvement and rehabilitation<br />\r\nManagement of crop rotation<br />\r\nCultivation of green cover crops<br />\r\nRainwater harvesting<br />\r\nIrrigation<br />\r\nAgroforestry (land-use systems and practices where woody perennials are deliberately integrated with crops and/or animals on the same land management unit).<br />\r\nConservation agriculture (combination of minimum mechanical soil disturbance, managing the top soil to create a permanent organic soil cover, and crop rotation with more than two crop species). (EURF Level 2 Indicator 6)</p>\r\n', '<p>Project M&amp;E system</p>\r\n', 'primary', '0'), ('84', '16', '7921', 'Volume of financial commitments (and disbursements) by SUN donors ', 'currency', '<p><a href=\"file:///\\\\Net1\\devco\\DG\\06\\10.%20Results\\8.%20Sector%20%20Indicator%20Guidance\\Sector%20indicator%20guidance%202016\\Agriculture%20&amp;%20Nutrition\\Indicators\\SUN-Donor-Resource-Tracking.pdf\">SUN Donor Network Resource Tracking methodology</a></p>\r\n', '<p><a href=\"http://scalingupnutrition.org/sun-supporters/sun-donor-network/\">SUN Donor Resource Network</a></p>\r\n', 'secondary', '0'), ('146', '16', '7909', 'Status of a budgeted national multi-sector plan/strategy for nutrition ', 'Qualitative', '<p><em>Score for this indicator will have to be based on an expert assessment (by Unit C1), and there need to be clear assessment criteria (agreed with partner countries) &ndash; for example as part of the update of Nutrition country fiches</em></p>\r\n', '<p>Monitored by DEVCO HQ (C1)</p>\r\n\r\n<p>Nutrition country fiche</p>\r\n', 'secondary and expert assessment', '0'), ('150', '16', '7908', 'Percentage of research programmes supported by EU delivering on agreed targets in the key topics of: food security, poverty reduction, nutrition, natural resource management and resilience.  ', 'Percentage ', '', '<p>Monitored by DEVCO HQ (C1)</p>\r\n', 'secondary', '0'), ('147', '16', '7908', 'Extent to which regional/national policy documents or sector strategies are making use of statistical information made available through information systems', 'Qualitative', '<p>Expert assessment needed</p>\r\n', '<p>Project M&amp;E system, government documents, expert analysis</p>\r\n', 'primary, secondary and expert assessment', '0'), ('148', '16', '7908', 'Number of countries benefitting from National Information Platforms on Nutrition (NIPN) ', 'Number of countries ', '', '<p>Monitored by DEVCO HQ (C1)</p>\r\n\r\n<p>NIPN Reports</p>\r\n', 'secondary', '0'), ('149', '16', '7908', 'Number of countries implementing food fortification actions ', '